'use strict';

module.exports = {
    config: {
        events: {
            dom: {
                hello: {
                    event: 'ready',
                    selector: '#hello',
                    sequences: [
                        {
                            name: 'display',
                            input: {
                                content: 'Hello'
                            }
                        }
                    ]
                },
                space: {
                    event: 'ready',
                    selector: '#hello-space',
                    sequences: [
                        {
                            name: 'display',
                            input: {
                                content: require('space/index')
                            }
                        }
                    ]
                }
            }
        },
        sequences: {
            display: {
                stream: {
                    content: {
                        type: 'string',
                        required: true
                    }
                },
                operations: [
                    {
                        service: 'danf:manipulation.callbackExecutor',
                        order: 0,
                        method: 'execute',
                        arguments: [
                            function(target, content) {
                                target.innerText = content;
                                target.removeAttribute('style');
                            },
                            '!event.target!',
                            '@content@'
                        ]
                    }
                ]
            }
        }
    }
};